import 'package:cloud_firestore/cloud_firestore.dart';class ChatMessageModel {  final String id;  final String chatRoomId;  final String senderId;  final String message;  final DateTime timestamp;  final bool isRead;  ChatMessageModel({    required this.id,    required this.chatRoomId,    required this.senderId,    required this.message,    required this.timestamp,    this.isRead = false,  });  factory ChatMessageModel.fromFirestore(DocumentSnapshot doc) {    final data = doc.data() as Map<String, dynamic>;    return ChatMessageModel(      id: doc.id,      chatRoomId: data['chatRoomId'] ?? '',      senderId: data['senderId'] ?? '',      message: data['message'] ?? '',      timestamp: (data['timestamp'] as Timestamp).toDate(),      isRead: data['isRead'] ?? false,    );  }  Map<String, dynamic> toMap() {    return {      'chatRoomId': chatRoomId,      'senderId': senderId,      'message': message,      'timestamp': Timestamp.fromDate(timestamp),      'isRead': isRead,    };  }}
