import 'package:cloud_firestore/cloud_firestore.dart';class FAQItem {  final String id;  final String question;  final String answer;  final String category;  final int order;  final bool isActive;  FAQItem({    required this.id,    required this.question,    required this.answer,    required this.category,    required this.order,    this.isActive = true,  });  factory FAQItem.fromFirestore(DocumentSnapshot doc) {    final data = doc.data() as Map<String, dynamic>;    return FAQItem(      id: doc.id,      question: data['question'] ?? '',      answer: data['answer'] ?? '',      category: data['category'] ?? '',      order: data['order'] ?? 0,      isActive: data['isActive'] ?? true,    );  }  Map<String, dynamic> toMap() {    return {      'question': question,      'answer': answer,      'category': category,      'order': order,      'isActive': isActive,    };  }}
