import 'package:cloud_firestore/cloud_firestore.dart';class Inquiry {  final String id;  final String userId;  final String productId;  final String message;  final DateTime createdAt;  final String status;  Inquiry({    required this.id,    required this.userId,    required this.productId,    required this.message,    required this.createdAt,    required this.status,  });  factory Inquiry.fromFirestore(DocumentSnapshot doc) {    final data = doc.data() as Map<String, dynamic>;    return Inquiry(      id: doc.id,      userId: data['userId'] ?? '',      productId: data['productId'] ?? '',      message: data['message'] ?? '',      createdAt: (data['createdAt'] as Timestamp).toDate(),      status: data['status'] ?? 'pending',    );  }  Map<String, dynamic> toMap() {    return {      'userId': userId,      'productId': productId,      'message': message,      'createdAt': Timestamp.fromDate(createdAt),      'status': status,    };  }}
